import maya.cmds as mc
import maya.OpenMaya as om

def createCtrl(obj,name,grpBol,rotBol,scaleVal):

	if mc.objExists(name):
		om.MGlobal.displayError('The Name already Exists...... :(')
		return 0
	
	if not name:
		om.MGlobal.displayWarning('Enter a Name....')
		return 0

	trans=mc.group(n=name,em=1)
	curve=mc.createNode('nurbsCurve',n=name+'Shape',p=trans)
	mc.select(trans)
	
	if rotBol:
		mc.setAttr(trans+'.rotateOrder',k=1,cb=1)
	
	if grpBol:
		grpName='Grp_' + name
		splName=name.split('_')
		if len(splName) > 1:
			grpName='Grp_'+ splName[1]
		grpTrans=mc.group(trans,n=grpName)
		mc.select(grpTrans)
	
