#scaling
def scaleCtrl(objects,scaleValue):
	sel=mc.ls(sl=1)
	if objects != None:
		sel = [objects]
	for each in sel:
	    getShapes=mc.listRelatives(each,c=True,s=True)
	    Cvs=0
	    if getShapes:
		    for shape in getShapes:
		        spans=mc.getAttr(shape+'.spans')
		        degree=mc.getAttr(shape+'.degree')
		        form=mc.getAttr(shape+'.form')
		        if form==0:
		            Cvs=spans + degree
		        else:
		            Cvs=spans
		        for i in range (Cvs):
		            getCvPos=mc.xform((shape+'.cv[%d]'% i),q=True,t=True)
		            mc.xform((shape+'.cv[%d]'% i),t=(getCvPos[0]*scaleValue,getCvPos[1]*scaleValue,getCvPos[2]*scaleValue))
#
def optMenuSwitch(dim):
	getMI=mc.optionMenu('ctrlsOM',q=1,ils=1)
	if getMI:
		mc.deleteUI(getMI,mi=1)
	if dim == '2D':
		dimCtrls=['square','triangle','diamond','pentagon','octagon','locator','rudder','star4','star7',
		'star12','crossbox','arrow','doublearrow','quadarrow']
	if dim == '3D':
		dimCtrls=['cube','sphere','capsule','pyramid','prism','tri','pentagonPrism','locatorPrism','rudderPrism',
		'point4Prism','starPrism','point7Prism','diamondPrism','crossPrism','arrowPrism','arrowDPrism','arrowQPrism']
	for obj in dimCtrls:
		mc.menuItem(label=obj.capitalize(),p='ctrlsOM')
		
def ar_controlMaker():
	#Main window
	#UI exists check
	if mc.window('winCtrlMaker',exists = 1):
		mc.deleteUI('winCtrlMaker',window = 1)
	if mc.windowPref('winCtrlMaker',exists = 1):
		mc.windowPref('winCtrlMaker',r = True)
	
	win = mc.window('winCtrlMaker',t = "Create Controls..",w=300,h=225)
	mc.formLayout('ctrlMakerFoL')
	
	mc.text('ctrlNamTX',l='Name -->>', fn='boldLabelFont')
	mc.textField('NameTF' ,text='Ctrl_',h=25,w=165)
	mc.radioButtonGrp('ctrlTypeRBG',l='Dimension : ',la2=['2D', '3D'], nrb=2,cw3=[75,50,50],sl=1,on1=lambda event:optMenuSwitch('2D'),on2=lambda event:optMenuSwitch('3D'))
	mc.optionMenu('ctrlsOM', label='Type : ',h=25)
	optMenuSwitch('2D')
	mc.text('scaleTX',l='Scale --> ')
	mc.floatField('scaleFF',minValue=.1, maxValue=100,v=1, precision=1, step=.1 )
	mc.checkBox('grpBolCB',l=' Group')
	mc.checkBox('rotOrderCB',l=' RotationOrder')
	mc.button('crtBTN',l='Create Control',w=293,h=30,bgc=[.9,.9,0],c=lambda event :createCtrl((mc.optionMenu('ctrlsOM',q=1,v=1).lower()),(mc.textField('NameTF',q=1,tx=1)),(mc.checkBox('grpBolCB',q=1,v=1)),(mc.checkBox('rotOrderCB',q=1,v=1)),(mc.floatField('scaleFF',q=1,v=1))))
	mc.setParent('..')
	
	mc.formLayout('ctrlMakerFoL' , edit = True , attachForm=[('ctrlNamTX', 'top', 18) ,('ctrlNamTX', 'left', 30),
	('NameTF', 'top', 15) ,('NameTF', 'left', 90),
	('ctrlTypeRBG', 'top', 50) ,('ctrlTypeRBG', 'left', 60),
	('ctrlsOM', 'top', 80) ,('ctrlsOM', 'left', 20),
	('scaleTX', 'top', 82) ,('scaleTX', 'left', 170),
	('scaleFF', 'top', 80) ,('scaleFF', 'left', 215),
	('grpBolCB', 'top', 120) ,('grpBolCB', 'left', 60),
	('rotOrderCB', 'top', 120) ,('rotOrderCB', 'left', 150),
	('crtBTN', 'top', 150)])
	
	mc.showWindow('winCtrlMaker')


ar_controlMaker()


